package net.lastcraft.base.skin;

import lombok.AllArgsConstructor;
import lombok.EqualsAndHashCode;
import lombok.Getter;
import lombok.ToString;

import java.util.concurrent.TimeUnit;

@AllArgsConstructor
@Getter
@EqualsAndHashCode
@ToString
public class Skin {

    public static final Skin SKIN_REWARD = createSkin("", "");
    public static final Skin SKIN_SHOP = createSkin( "", "");
    public static final Skin SKIN_SPECTATOR = createSkin("", "");

    public static final Skin DEFAULT_SKIN = createSkin("", "");

    public static final Skin SKIN_SHOP_GAME = createSkin("", "");

    private final String skinName;
    private final String value;
    private final String signature;
    private final SkinType skinType;
    private final long updateTime;

    public Skin(String skinName, String value, String signature, SkinType skinType) {
        this(skinName, value, signature, skinType, 0);
    }

    public boolean isExpired() {
        return updateTime != 0 && System.currentTimeMillis() > updateTime + TimeUnit.HOURS.toMillis(24);
    }

    public static Skin createSkin(String value, String signature) {
        return new Skin("SKIN_DEFAULT", value, signature, SkinType.CUSTOM);
    }
}
